package javaPrograms;

public class VariablesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Integer data type
		
		int a= 10;
		int b=20;
		System.out.println("The value of variable b is:" + a);
		System.out.println("The value of variable b is:" + b);
		
		// Double data type
		
		double d1 = 2.344;
		double d2 = 34.56;
		System.out.println("The value of variable d1 is:" + d1);
		System.out.println("The value of variable d2 is:" + d2);
		
		//Character data type
		
		char c1 = 'a';
		System.out.println("The value of character variable c1 is:" + c1);
		
		// String variable
		
		String s1 = "Selenium";
		String s2 = "Selenium@123";
		System.out.println("The value of variable s1 is:" + s1);
		System.out.println("The value of variable s2 is:" + s2);
		
		
		
		
		
		
		
	}

}
